import { gql } from '@apollo/client';
export const getDepartments = async (apolloClient) => {
  const {
    error,
    data
  } = await apolloClient.query({
    query: gql`
      query getDepartments {
        departments {
          id
          slug
          name
          categories {
            id
            slug
            name
            subcategories {
              id
              slug
              name
            }
          }
        }
      }
    `
  });
  return error !== null && error !== void 0 ? error : data.departments;
};
