      return JSON.parse(localStorage.getItem('orderItems')) || [];
    } catch (_error) {
      console.error((_error as SyntaxError).message);
      return null;
    }
  };


  const [order, setOrder] = useState<IPaymentOrder>(getOrderItemFromLS());

